glsl3.vert:
precision highp float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
#if USE_LOCAL
  layout(std140) uniform CCLocal {
    highp mat4 cc_matWorld;
    highp mat4 cc_matWorldIT;
    highp vec4 cc_lightingMapUVParam;
    highp vec4 cc_localShadowBias;
    highp vec4 cc_reflectionProbeData1;
    highp vec4 cc_reflectionProbeData2;
    highp vec4 cc_reflectionProbeBlendData1;
    highp vec4 cc_reflectionProbeBlendData2;
  };
#endif
in vec3 a_position;
in vec2 a_texCoord;
in vec4 a_color;
out vec4 v_color;
out vec2 v_uv0;
#if USE_TEXTURE
  in vec2 a_uv0;
#endif
vec4 vert (){
  vec4 pos = vec4(a_position, 1);
  #if USE_LOCAL
    pos = cc_matWorld * pos;
  #endif
  #if USE_PIXEL_ALIGNMENT
    pos = cc_matView * pos;
    pos.xyz = floor(pos.xyz);
    pos = cc_matProj * pos;
  #else
    pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  v_uv0 = a_texCoord;
  return pos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
  in vec2 v_uv0;
  uniform sampler2D cc_spriteTexture;
#endif
#if SHOW_INNER_GLOW
  layout(std140) uniform glow {
    vec4 glowColor;
    float glowColorSize;
    float glowThreshold;
  };
  vec4 getTextureColor(sampler2D mainTexture, vec2 v_uv0) {
    if (v_uv0.x > 1.0 || v_uv0.x < 0.0 || v_uv0.y > 1.0 || v_uv0.y < 0.0) {
      return vec4(0.0, 0.0, 0.0, 0.0);
    }
    return CCSampleWithAlphaSeparated(mainTexture, v_uv0);
  }
float getColorAlpha(float angle, float dist) {
  float radian = radians(angle);
  vec4 color = getTextureColor(cc_spriteTexture, v_uv0 + vec2(dist * cos(radian), dist * sin(radian)));
  return color.a;
}
float getAverageAlpha(float dist) {
  float totalAlpha = 0.0;
  totalAlpha += getColorAlpha(0.0, dist);
  totalAlpha += getColorAlpha(30.0, dist);
  totalAlpha += getColorAlpha(60.0, dist);
  totalAlpha += getColorAlpha(90.0, dist);
  totalAlpha += getColorAlpha(120.0, dist);
  totalAlpha += getColorAlpha(150.0, dist);
  totalAlpha += getColorAlpha(180.0, dist);
  totalAlpha += getColorAlpha(210.0, dist);
  totalAlpha += getColorAlpha(240.0, dist);
  totalAlpha += getColorAlpha(270.0, dist);
  totalAlpha += getColorAlpha(300.0, dist);
  totalAlpha += getColorAlpha(330.0, dist);
  return totalAlpha * 0.0833;
}
float getGlowAlpha() {
  if (glowColorSize == 0.0) {
    return 0.0;
  }
  vec4 srcColor = getTextureColor(cc_spriteTexture, v_uv0);
  if (srcColor.a <= glowThreshold) {
    return srcColor.a;
  }
  float totalAlpha = 0.0;
  totalAlpha += getAverageAlpha(glowColorSize * 0.1);
  totalAlpha += getAverageAlpha(glowColorSize * 0.2);
  totalAlpha += getAverageAlpha(glowColorSize * 0.3);
  totalAlpha += getAverageAlpha(glowColorSize * 0.4);
  totalAlpha += getAverageAlpha(glowColorSize * 0.5);
  totalAlpha += getAverageAlpha(glowColorSize * 0.6);
  totalAlpha += getAverageAlpha(glowColorSize * 0.7);
  totalAlpha += getAverageAlpha(glowColorSize * 0.8);
  totalAlpha += getAverageAlpha(glowColorSize * 0.9);
  totalAlpha += getAverageAlpha(glowColorSize * 1.0);
  return totalAlpha * 0.1;
}
#endif
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, v_uv0);
    #if CC_USE_ALPHA_ATLAS_TEXTURE
      o.a *= CCSampleWithAlphaSeparated(cc_spriteTexture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  #if SHOW_INNER_GLOW
    vec4 color_dest = o;
    float alpha = getGlowAlpha();
    if (alpha > glowThreshold) {
      alpha = 1.0 - alpha;
      alpha = -1.0 * (alpha - 1.0) * (alpha - 1.0) * (alpha - 1.0) * (alpha - 1.0) + 1.0;
    }
    vec4 color_src = glowColor * alpha;
    o = color_src * color_src.a + color_dest;
    return o;
  #endif
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

